public class bitwise {
    public static void main(String[] args) {
        int a = 5;
        int b = 3;
        //5 is converted to 0101 and 3 is converted to 0011 and 'AND' operation 
        System.out.println("a & b = " + (a & b));
        //5 is converted to 0101 and 3 is converted to 0011 and 'OR' operation
        System.out.println("a | b = " + (a | b));
    }

}
