public class conditional {
    public static void main(String[] args) {
        int a = 1;
        int b = 2;

        if ((a == 3) & (b == 2)){
            System.out.println("true");                    
        }
        if ((a == 5) || (b == 2)){
            System.out.println("true");                    
        }
    }

}
